// -------------------------------// adapted from Kevin T. Manley// CSE 593// -------------------------------package ResImpl;import java.io.*;// Resource manager data itempublic abstract class RMItem implements Serializable {	RMItem() {		super();	}	public RMItem clone() {		try {			ByteArrayOutputStream bo = new ByteArrayOutputStream();			ObjectOutputStream oo = new ObjectOutputStream(bo);			oo.writeObject(this);			ByteArrayInputStream bi = new ByteArrayInputStream(bo.toByteArray());			ObjectInputStream oi = new ObjectInputStream(bi);			return (RMItem) oi.readObject();		} catch (IOException e) {			System.out.println(e.getMessage());			return null;		} catch (ClassNotFoundException e) {			System.out.println(e.getMessage());			return null;		}		// return this.clone();	}}